import React, { useContext, useState, useEffect } from 'react';
import Modal from 'react-bootstrap/Modal';
import  {Button, Dropdown}  from 'react-bootstrap';
import { Form,Row,Col } from 'react-bootstrap';
import {Context} from "../../index";
import { createService, fetchTroublescores, fetchTypes, fetchServices } from '../../http/serviceAPI';
import { observer } from 'mobx-react-lite';

const CreateService = observer(({show, onHide}) => {
    const {service} = useContext(Context)
    const [name, setName] = useState ('')
    const [price, setPrice] = useState (0)
    const [file, setFile] = useState (null)
    const [info, setInfo] = useState ([])

    useEffect(() =>{
      fetchTypes().then(data => service.setTypes(data))
      fetchTroublescores().then(data => service.setTroublescores(data))
    }, [])


  
    const addInfo = () => {
      setInfo([...info, {title: '', description: '', number : Date.now()}])
    }

    const removeInfo = (number) => {
      setInfo(info.filter(i => i.number !== number))
    }

      const changeInfo = (key, value, number) => {
        setInfo(info.map(i => i.number === number ? {...i, [key]: value}: i))
      }


    const selectFile = e => {
      setFile(e.target.files[0])
    }
    
    const addService = () => {
      const formData = new FormData()
      formData.append('name', name)
      formData.append('price', `${price}`)
      formData.append('img', file)
      formData.append('troublescoreId', service.selectedTroublescore.id)
      formData.append('typeId', service.selectedType.id)
      formData.append('info', JSON.stringify(info))
      createService(formData).then(data => onHide())
    }

    return (
        <Modal
        show={show}
        onHide={onHide}
        
        centered
      >
        <Modal.Header closeButton>
          <Modal.Title id="contained-modal-title-vcenter">
            Добавить услугу
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Dropdown className="mt-2 mb-2">

              <Dropdown.Toggle>{service.selectedType.name || "Выберите тип"}</Dropdown.Toggle>
              <Dropdown.Menu>
                {service.types.map(type =>
                  <Dropdown.Item  
                  onClick = {() => service.setSelectedType(type)} 
                  key = {type.id}
                  
                  > 
                  
                     {type.name}
                  </Dropdown.Item>
                  )}
              </Dropdown.Menu>
            </Dropdown>
            <Dropdown className="mt-2 mb-2">
              <Dropdown.Toggle>{service.selectedTroublescore.name || "Выберите сложность"}</Dropdown.Toggle>
              <Dropdown.Menu>
                {service.troublescores.map(troublescore =>
                  <Dropdown.Item onClick = {() => service.setSelectedTroublescore(troublescore)} key = {troublescore.id}> {troublescore.name}</Dropdown.Item>
                  )}
              </Dropdown.Menu>
            </Dropdown>


                <Form.Control
                value = {name}
                onChange = {e => setName(e.target.value)}
                  className="mt-3"
                  placeholder="Введите название услуги"
                />

                 <Form.Control
                 value = {price}
                 onChange = {e => setPrice(Number(e.target.value))}
                  className="mt-3"
                  placeholder="Введите стоимость услуги"
                  type="number"
                />

                <Form.Control
                  className="mt-3"
                  type="file"
                  onChange = {selectFile}
                />
                <hr/>
                  <Button
                    variant={"outline-dark"}
                    onClick={addInfo}
                  >
                    Добавить новое свойство
                  </Button>

                  {info.map(i => 
                        <Row className="mt-4" key={i.number}>
                            <Col md={4}>
                              <Form.Control
                                value = {i.title}
                                onChange = {(e) => changeInfo('title', e.target.value, i.number)}
                                placeholder="Введите название области услуги"
                              />
                            </Col>
                            <Col md={4}>
                              <Form.Control
                                value = {i.description}
                                onChange = {(e) => changeInfo('description', e.target.value, i.number)}
                               placeholder="Введите описание услуги"
                              />
                            </Col>
                            <Col md={4}>
                             <Button 
                              onClick={()=> removeInfo(i.number)}
                              variant={"outline-danger"}>
                               Удалить
                              </Button>
                            </Col>
                        </Row>
                      )
                  }

          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="outline-danger" onClick={onHide}>Закрыть</Button>
          <Button variant="outline-success" onClick={addService}>Добавить</Button>
        </Modal.Footer>
      </Modal>
       
    );
});

export default CreateService;
