import {makeAutoObservable} from "mobx";

export default class DeviceStore {
    constructor(){
       
        this._types = [
            {id: 1, name: 'Бухучет'},
            {id: 2, name: 'Налоги'},
            {id: 3, name: 'Консультации'}
        ]

        this._troublescores = [
            {id: 2, name: 'Сложная'},
            {id: 4, name: 'Средняя'},
            {id: 5, name: 'Легкая'},
        ]

        this._services = [
            {id: 2, name: 'Аутсорсинг бухгалтерских услуг', price: 100000, rating: 0, img: `c8745350-5681-41c0-9a31-674f6936642e.jpg`},
            {id: 3, name: 'Налоговое сопровождение', price: 100000, rating: 0, img: `edd69ba0-7f2b-408b-a610-4ab9cdf6d1a8.jpg`},
            {id: 4, name: 'Оптимизация налогов', price: 100000, rating: 0, img: `e9f81654-a0b3-4670-bb8a-8f71a896df68.jpg`},
            {id: 7, name: 'Бухгалтерское сопровождение', price: 100000, rating: 0, img: `a9a9ccff-f510-44eb-b1ac-04480c340047.jpg`},
            {id: 8, name: 'Обслуживание юридических лиц', price: 100000, rating: 0, img: `1d16e0af-71fc-48cd-adf5-772f2596902c.jpg`},
            {id: 9, name: 'Ликвидация ООО', price: 100000, rating: 0, img: `aafd3155-bf6f-4869-9aff-3e4827d93e64.jpg`},
        ]
        this._selectedType = {}
        this._selectedTroublescore = {}
        makeAutoObservable(this)
    }


    setTypes(types) {
        this._types = types
    }

    setTroublescores(troublescores) {
        this._troublescores = troublescores
    }
    setServices(services) {
        this._services = services
    }

    setSelectedType(type) {
        this._selectedType = type
    }

    setSelectedTroublescore(troublescore) {
        this._selectedTroublescore = troublescore
    }

    //вызываем верхние фнкции
    get types(){    
        return this._types
    }

    get troublescores(){
        return this._troublescores
    }

    get services(){    
        return this._services
    }

    get selectedType() {    
        return this._selectedType
    }

    get selectedTroublescore() {    
        return this._selectedTroublescore
    }
}
