import React from 'react';
import { Button, Card, Col, Container, Image, Row } from 'react-bootstrap';
import logo from './imagepack/аутсорсинг.png';

const ServicePage = () => {
  const service = {id: 2, name: 'Аутсорсинг бухгалтерских услуг', price: 3500, rating: 0, img: logo}
  const description = [
    {id: 1, title: 'Первая характеристика1', description:'информанция номер 1'},
    {id: 2, title: 'Первая характеристика2', description:'информанция номер 2'},
    {id: 3, title: 'Первая характеристика3', description:'информанция номер 3'},
    {id: 4, title: 'Первая характеристика4', description:'информанция номер 4'},
    {id: 5, title: 'Первая характеристика5', description:'информанция номер 5'},
  ]

  return (
    
     <Container className="mt-3">
       <Row>
        <Col md={4}>
            <Image width={300} height={300} src={service.img}/>
        </Col>
        <Col md={4}>
          sdfsfsdfd
        </Col>
        <Col md={4} className="">
            <Row className="d-flex flex-column align-items-center">
                <h2 className="ml-3 ">{service.name}</h2>
            </Row>
              
                  
          <Card 
            className="d-flex flex-column  justify-content-around mt-5 p-2"
            style={{width: 300, height: 100, fontSize: 32, border: '1px solid black'}}
          >
              <h3 >От: {service.price} р/месяц.</h3>
              <Button className="mt-2 pr-2" variant={"outline-dark"}>
                Добавить услугу
              </Button>
          </Card>
        </Col>
        
        
       </Row>
       <Row className="d-flex flex-column mt-3 mb-3 ml-3  ">
         <h1 className="mt-1 ml-n3  ">Общая информация</h1>
         {description.map((info, index) =>
            <Row key={info.id} style={{background: index % 2 === 0 ? 'lightgray' : 'transparent', padding:10}}>
                {info.title}: {info.description}
            </Row>
          )}
       </Row>
     </Container>
  );
};

export default ServicePage;
