import {makeAutoObservable} from "mobx";
import logo from '../pages/imagepack/аутсорсинг.png';
import logo2 from '../pages/imagepack/оптимизацияналогов.png';
import logo3 from '../pages/imagepack/ведение учета.png';
import logo4 from '../pages/imagepack/бухконсалтинг.png';
import logo5 from '../pages/imagepack/юрлица.png';
import logo6 from '../pages/imagepack/налогообложение.png';
export default class DeviceStore {
    constructor(){
       
        this._types = [
            {id: 1, name: 'Бухучет'},
            {id: 2, name: 'Налоги'},
            {id: 3, name: 'Консультации'}
        ]

        this._troublescores = [
            {id: 2, name: 'Все услуги'},
            {id: 4, name: 'ООО'},
            {id: 5, name: 'Заявления'},
            {id: 6, name: 'Прочие услуги'},
            {id: 6, name: 'ИП'},
        ]
        //услуги
        this._services = [
            {id: 2, name: 'Аутсорсинг бухгалтерских услуг', price: 3500, rating: 0, img: logo},
            {id: 3, name: 'Ведение учета', price: 4200, rating: 0, img: logo3},
            {id: 4, name: 'Оптимизация налогов', price: 6600, rating: 0, img: logo2},
            {id: 7, name: 'Бухгалтерский консалтинг', price: 3700, rating: 0, img: logo4},
            {id: 8, name: 'Обслуживание юридических лиц', price: 4200, rating: 0, img: logo5},
            {id: 9, name: 'Подбор системы налогообложения', price: 4000, rating: 0, img: logo6},
        ]
        this._selectedType = {}
        this._selectedTroublescore = {}
        makeAutoObservable(this)
    }


    setTypes(types) {
        this._types = types
    }

    setTroublescores(troublescores) {
        this._troublescores = troublescores
    }
    setServices(services) {
        this._services = services
    }

    setSelectedType(type) {
        this._selectedType = type
    }

    setSelectedTroublescore(troublescore) {
        this._selectedTroublescore = troublescore
    }

    //вызываем верхние фнкции
    get types(){    
        return this._types
    }

    get troublescores(){
        return this._troublescores
    }

    get services(){    
        return this._services
    }

    get selectedType() {    
        return this._selectedType
    }

    get selectedTroublescore() {    
        return this._selectedTroublescore
    }
}
