import React, { useContext, useState } from 'react';
import Modal from 'react-bootstrap/Modal';
import  {Button, Dropdown}  from 'react-bootstrap';
import { Form,Row,Col } from 'react-bootstrap';
import {Context} from "../../index";


const CreateService = ({show, onHide}) => {
    const {service} = useContext(Context)
    const [name, setName] = useState ('')
    const [price, setPrice] = useState (0)
    const [file, setFile] = useState (null)
    const [troublescore, setTroublescore] = useState (null)
    const [type, setType] = useState (null)
    const [info, setInfo] = useState ([])
  
    const addInfo = () => {
      setInfo([...info, {title: '', description: '', number : Date.now()}])
    }

    const removeInfo = (number) => {
      setInfo(info.filter(i => i.number !== number))
    }

    const selectFile = e => {
      console.log(e.target.files)
    }

    return (
        <Modal
        show={show}
        onHide={onHide}
        
        centered
      >
        <Modal.Header closeButton>
          <Modal.Title id="contained-modal-title-vcenter">
            Добавить услугу
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Dropdown className="mt-2 mb-2">
              <Dropdown.Toggle>Выберите тип</Dropdown.Toggle>
              <Dropdown.Menu>
                {service.types.map(type =>
                  <Dropdown.Item key = {type.id}> {type.name}</Dropdown.Item>
                  )}
              </Dropdown.Menu>
            </Dropdown>
            <Dropdown className="mt-2 mb-2">
              <Dropdown.Toggle>Выберите вид</Dropdown.Toggle>
              <Dropdown.Menu>
                {service.troublescores.map(troublescore =>
                  <Dropdown.Item key = {troublescore.id}> {troublescore.name}</Dropdown.Item>
                  )}
              </Dropdown.Menu>
            </Dropdown>
                <Form.Control
                  className="mt-3"
                  placeholder="Введите название услуги"
                />

                 <Form.Control
                  className="mt-3"
                  placeholder="Введите стоимость услуги"
                  type="number"
                />

                <Form.Control
                  className="mt-3"
                  type="file"
                  onChange = {selectFile}
                />
                <hr/>
                  <Button
                    variant={"outline-dark"}
                    onClick={addInfo}
                  >
                    Добавить новое свойство
                  </Button>

                  {info.map(i => 
                        <Row className="mt-4" key={i.number}>
                            <Col md={4}>
                              <Form.Control
                                placeholder="Введите название области услуги"
                              />
                            </Col>
                            <Col md={4}>
                              <Form.Control
                               placeholder="Введите описание услуги"
                              />
                            </Col>
                            <Col md={4}>
                             <Button 
                              onClick={()=> removeInfo(i.number)}
                              variant={"outline-danger"}>
                               Удалить
                              </Button>
                            </Col>
                        </Row>
                      )
                  }

          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="outline-danger" onClick={onHide}>Закрыть</Button>
          <Button variant="outline-success" onClick={onHide}>Добавить</Button>
        </Modal.Footer>
      </Modal>
       
    );
};

export default CreateService;
